import React from "react";

export default function TodoList(props) {
  let idUnique = 0;
  const [checked, setChecked] = React.useState(false);

  const handleChangeset = event => {
    if (event.target.checked) {
      console.log('✅ Checkbox is checked');
    } else {
      console.log('⛔️ Checkbox is NOT checked');
    }
    setChecked(current => !current);
  };

  return (
    <section>
      <h2  className="text-2xl font-bold m-5">Todos List</h2>
      <ul>
        {props.todos.map((todo) => (
          <li key={idUnique++}>
            {/* <input className="w-4 h-4 text-pink-500 bg-gray-100 rounded border-gray-300 focus:ring-pink-500 dark:focus:ring-pink-600 dark:ring-offset-gray-700 focus:ring-2 dark:bg-gray-600 dark:border-gray-500"type="checkbox" */}
            <input id="purple-checkbox" type="checkbox" value="" class="w-4 h-4 text-purple-600 bg-gray-100 rounded border-gray-300 focus:ring-purple-500 dark:focus:ring-purple-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600"
            defaultChecked={checked}
            onChange={handleChangeset}          
            />{todo}
          </li>
        ))}
      </ul>
    </section>
  );
}
